import { makeAutoObservable } from "mobx"


class Toodo {
    toodos = [
        {id: 1, title: 'some text', flag: false},
        {id: 2, title: 'some text', flag: true},
        {id: 3, title: 'some text', flag: false},
    ]
    constructor() {
        makeAutoObservable(this)
    }
    addToodo(toodo) {this.toodos.push(toodo)}
    removeToodo(id) {
        console.log(id)
        this.toodos = this.toodos.filter(toodo => toodo.id !== id)
    }
    compliteToodo(id) {
        console.log(id)
        this.toodos = this.toodos.map(toodo => toodo.id !== id ? {...toodo, flag: !toodo.flag}: toodo)
    }
}

export default new Toodo()
