import {makeAutoObservable, makeObservable} from 'mobx'

class Counter {
    count = 0
    constructor() {
        makeAutoObservable(this)
    }

    increment() {
        this.count = this.count + 1
    }
    decrement() {
        this.count = this.count - 1
    }
}

export default new Counter()
